class AVTime:
    __slots__ = ("seconds", "hops", "frames", "_analysis")

    def __init__(self, seconds: float, analysis: "AudioAnalysisResult"):
        self.seconds = seconds
        self.hops = int(round(seconds * analysis.hops_per_second(), 8))
        self.frames = self.hops / analysis.hops_per_frame()
        self._analysis = analysis  # btw reference is cheap memory wise!

    def __repr__(self) -> str:
        return self.__str__()

    def __str__(self) -> str:
        return f"{self.seconds:.3f}s ({self.hops} hops, {self.frames:.2f} frames)"

    @property
    def timecode(self):
        return seconds_to_timecode(self.seconds, self._analysis.fps)

