def on_scroll(event: MouseEvent):
    if event.inaxes != plotter.ax:
        return
    base_scale = 0.9
    cur = plotter.get_x_limits()
    xdata = event.xdata
    if event.button == 'up':
        scale_factor = base_scale
    elif event.button == 'down':
        scale_factor = 1 / base_scale
    else:
        scale_factor = 1
    new_width = cur.width * scale_factor
    relx = (xdata - cur.start) / cur.width
    plotter.set_x_limits(xdata - new_width * relx, xdata + new_width * (1 - relx))
