class YLimits:
    def __init__(self, lower: float, upper: float):
        self.lower = lower
        self.upper = upper


def get_y_limits() -> YLimits:
    lower, upper = plotter.ax.get_ylim()
    return YLimits(lower, upper)


def pan_up(event: KeyEvent):
    cur_limits = get_y_limits()
    shift = (cur_limits.upper - cur_limits.lower) * 0.1
    plotter.set_y_limits(cur_limits.lower + shift, cur_limits.upper + shift)


def pan_down(event: KeyEvent):
    cur_limits = get_y_limits()
    shift = (cur_limits.upper - cur_limits.lower) * 0.1
    plotter.set_y_limits(cur_limits.lower - shift, cur_limits.upper - shift)
