def _pan_y_by_factor(event: KeyEvent, factor: float) -> None:
    cur = plotter.get_y_limits()
    shift = cur.height * factor
    plotter.set_y_limits(cur.bottom + shift, cur.top + shift)


def pan_up(event: KeyEvent):
    _pan_y_by_factor(event, 0.1)


def pan_down(event: KeyEvent):
    _pan_y_by_factor(event, -0.1)
